<?php

namespace ShareThat\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * VideoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class VideoRepository extends EntityRepository
{
    public function saveVideo(\ShareThat\Entity\Video $video, array $values)
    {
        $video->setName($values['name']);
        $video->setShortName($values['shortName']);

        $this->getEntityManager()->persist($video);
    }

    public function removeVideo($id)
    {
        $em    = $this->getEntityManager();
        $proxy = $em->getReference('\ShareThat\Entity\Video', $id);

        $em->remove($proxy);
    }
}